from browser import window
from surface import Surface

class Image(object):
    def __init__(self):
        self.load_image = window.load_image
        self.get_image = window.get_image

    def load(self, filename):
        if self.get_image(filename) == None:
            self.load_image(filename)
        img = self.get_image(filename)
        return Surface(size=(img.width, img.height), img=img)